import { Marker } from "react-google-maps";


export default class Pin extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      openMarker: null,
    };
  }

  setMarker = (key) =>{
    this.setState({
      openMarker: key
    })
  }

  render(){
    console.log(this.state, this.props)
    return(
        <Marker position={this.props.location} onClick={e => this.setMarker(this.props.key)}>

        </Marker>
    );
  }
}
