import React, { Component } from 'react';
import Filter from './Components/Filter';
import MyMap from './Components/Map';

export default class NeighborhoodPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      classes:[
        {title: 'Black Swan Yoga White Oak', location: {lat: 29.7818, lng: -95.3923}},
        {title: 'Black Swan Yoga Kirby', location: {lat: 29.7249, lng: -95.4190}},
        {title: 'YogaWorks Midtown', location: {lat: 29.7491, lng: -95.3730}},
        {title: 'YogaWorks Heights', location: {lat: 29.8026, lng: -95.4078}},
        {title: 'SALT Power Yoga', location: {lat: 29.7701, lng: -95.4228}},
        {title: 'BIG Power Yoga', location: {lat: 29.7607, lng: -95.3990}}],
        openMarker: null,
    };
  }

  componentDidMount(){

    for(index in this.state.classes){
      let lat=this.state.classes[index].location.lat
      let lng= this.state.classes[index].location.lng
      let classCopy= this.state.classes
      fetch(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/f499640fa3285352db4d8446365470b7/${lat},${lng}?exclude=[minutely,daily,alerts,flags,hourly]`)
        .then(resp=>resp.json())
        .then(data => {
          classCopy[index].weather = data.currently
          this.setState({
            classes: classCopy
          })
        })
    }
  }


  setMarker = (key) =>{
    this.setState({
      openMarker: key
    })
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <Filter />
        <MyMap classes= {this.state.classes} setMarker={this.setMarker} openMarker={this.state.openMarker}/>
      </div>
    );
  }
}
