import React, { Component } from 'react';
import Filter from './Components/Filter';
import MyMap from './Components/Map';

export default class NeighborhoodPage extends Component {
  constructor(props) {
    super(props);
    this.state = {

        classes:[
               {title: "Central Park, New York, NY", location: {lat: 40.7850, lng: -73.9682}},
               {title: "Griffith Park, Los Angeles, CA", location: {lat: 31.1366, lng: -118.2942}},
               {title: "Lincoln Park, Chicago, IL", location: {lat: 41.9214, lng: -87.6513}},
               {title: "Zilker Park, Austin, TX", location: {lat: 30.2670, lng: -97.7729}},
               {title: "Golden Gate Park, San Francisco, CA", location: {lat: 37.7694, lng: -122.4862}}],
        openMarker: null,
    };
  }

  componentDidMount(){

    for(let index in this.state.classes){
      let lat=this.state.classes[index].location.lat
      let lng= this.state.classes[index].location.lng
      let classCopy= this.state.classes
      fetch(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/f499640fa3285352db4d8446365470b7/${lat},${lng}?exclude=[minutely,daily,alerts,flags,hourly]`)
        .then(resp=>resp.json())
        .then(data => {
          classCopy[index].weather = data.currently
          this.setState({
            classes: classCopy
          })
        })
    }
  }


  setMarker = (key) =>{
    this.setState({
      openMarker: key
    })
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <Filter />
        <MyMap classes= {this.state.classes} setMarker={this.setMarker} openMarker={this.state.openMarker}/>
      </div>
    );
  }
}
