import React from "react";
import { Marker, InfoWindow } from "react-google-maps";


export default class Pin extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      openMarker: null,
    };
  }



  render(){
    console.log(this.props)
    return(
        <Marker position={this.props.location.location} onClick={e => this.props.setMarker(this.props.id)}>
          { this.props.openMarker === this.props.id ?
            <InfoWindow maxWidth={800} defaultPosition={ this.props.location } onCloseClick={e=>this.props.setMarker(null)}>
              <div>{this.props.location.weather.temperature}</div>
            </InfoWindow> : null
          }
        </Marker>
    );
  }
}
